mapper.update:
UPDATE loan_applications
SET user_id = #{userId}, loan_amount = #{loanAmount}, loan_term = #{loanTerm},
loan_interest_rate = #{loanInterestRate}, application_date = #{applicationDate}, status = #{status}
WHERE id = #{id}

mapper.select:
SELECT  loan_applications.*,users.username,users.phone_number
FROM loan_applications  LEFT JOIN users on  users.id = loan_applications.user_id
WHERE id = #{loanApplicationId}

SELECT loan_applications.*,users.username,users.phone_number
from loan_applications LEFT JOIN users on  users.id = loan_applications.user_id
where 1 = 1 and users.username like #{keyword} limit ${offset},${size}

SELECT count(*) as total
from loan_applications LEFT JOIN users on  users.id = loan_applications.user_id
where 1 = 1 and users.username like #{keyword}
